  constructor(props){
    super(props);
    console.log('hello')
  }
  state = {
    count: 0
  };
  add = () => {
    console.log('add');
    this.setState(current => ({count: current.count + 1}));
  };
  minus = () => {
    console.log('minus');
    this.setState(current => ({count: current.count - 1}));
  };

  componentDidMount(){
    console.log('component rendered')
  }

  componentDidUpdate(){
    console.log('I just updated')
  }

 render(){
   console.log("I'm rendering")
