import React from 'react';

const roaster = [
  {
    id: 1,
    name: 'Messi',
    image: './lineup1.jpg'
  },
  {
    id: 2,
    name: 'Suarez',
    image: './lineup2.jpg'
  }
];

function Lineup({name, picture}) {
  return (
    <div>
      <h1>Today's player is {name}</h1>
      <img src = {picture} alt = {name}/>
    </div>
  );
}

function RenderLineup(player) {
  return <Lineup name = {player.name} picture = {player.image} />
}

function App() {
  return (
    <div>
      {roaster.map(RenderLineup)}
    </div>
  );
}

export default App;
